INSERT INTO community_posts (id, user_id, content, post_type, category, tags, image_urls, likes_count, comments_count, is_featured, created_at) VALUES
(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', '✨ BRIDAL GLAM SHOWCASE ✨\n\n6 hours of artistry for this beautiful bride! Created a timeless look that photographs beautifully and lasts all day. Used airbrush foundation, hand-placed individual lashes, and custom lip color mixed to match her dress.\n\nThe glow when she saw herself in the mirror... pure magic 💕 Nothing beats making someone feel absolutely radiant on their special day!\n\n#BridalMakeup #WeddingGlam #MakeupArtist #BrideMakeup', 'showcase', 'makeup', ARRAY['bridal-makeup', 'wedding', 'airbrush'], ARRAY['https://images.unsplash.com/photo-1649972904349-6e44c42644a7'], 189, 56, true, NOW() - INTERVAL '1 day'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', '🌈 RAINBOW OMBRÉ NAILS 🌈\n\nSpent 4 hours perfecting this gradient technique! Each nail transitions through 7 colors using a tiny makeup sponge and steady hands. The key is working fast before the polish dries and blending while it''s still tacky.\n\nClient wanted something bold for Pride month - mission accomplished! 🏳️‍🌈\n\n#RainbowNails #PrideNails #OmbreNails #NailArt #GelNails', 'showcase', 'nails', ARRAY['rainbow-nails', 'ombre', 'pride', 'nail-art'], ARRAY['https://images.unsplash.com/photo-1526374965328-7f61d4dc18c5'], 234, 72, false, NOW() - INTERVAL '3 days'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'TRANSFORMATION TUESDAY! 💫\n\nFrom damaged, over-processed hair to healthy, glossy locks! This client had been bleaching at home for months. We did:\n\n🔹 Olaplex treatment series\n🔹 Cut off 6 inches of damage  \n🔹 Toned to perfect ash blonde\n🔹 Glossing treatment for shine\n\nSometimes less is more - health over length always! 💛\n\n#HairTransformation #OlaplexTreatment #HealthyHair #BlondeHair #HairRecovery', 'showcase', 'hair', ARRAY['hair-transformation', 'olaplex', 'blonde-hair', 'hair-health'], ARRAY['https://images.unsplash.com/photo-1506744038136-46273834b3fb'], 167, 43, false, NOW() - INTERVAL '5 days'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'Volume lash set perfection! 👁️✨\n\n3D volume using 0.03 lashes - 5-7 fans per natural lash. This technique creates incredible fullness while staying lightweight. Client has sparse natural lashes but look at this transformation!\n\nUsed C curl for natural eye shape, varied lengths 8-12mm. Total application time: 2.5 hours. She''s been wearing them for 4 weeks and they still look fresh!\n\n#VolumeLashes #3DLashes #LashExtensions #EyeLashes #LashArt', 'showcase', 'lashes', ARRAY['volume-lashes', '3d-lashes', 'lash-extensions'], ARRAY['https://images.unsplash.com/photo-1582562124811-c09040d0a901'], 145, 38, false, NOW() - INTERVAL '7 days'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'Microblading masterpiece! 🎨\n\nNatural hair-stroke technique for this client who lost her brows due to alopecia. We designed a shape that complements her bone structure perfectly. Used golden brown pigment to match her natural hair color.\n\nSeeing her confidence return is why I love this art form! 6 weeks healed, touch-up complete. These will last 1-2 years with proper care.\n\n#Microblading #BrowArt #AlopeciaSupport #EyebrowDesign #PermanentMakeup', 'showcase', 'brows', ARRAY['microblading', 'brow-art', 'alopecia', 'permanent-makeup'], ARRAY['https://images.unsplash.com/photo-1649972904349-6e44c42644a7'], 201, 67, false, NOW() - INTERVAL '9 days');

-- Questions/Polls
INSERT INTO community_posts (id, user_id, content, post_type, category, tags, image_urls, likes_count, comments_count, is_featured, created_at) VALUES
(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'POLL: What''s your go-to nail shape for clients? 💅\n\nI''ve been doing more coffin/ballerina shapes lately but wondering what everyone else is seeing as trending! Drop your top 3 in comments:\n\n🔸 Oval\n🔸 Round  \n🔸 Square\n🔸 Coffin/Ballerina\n🔸 Almond\n🔸 Stiletto\n\nAlso curious - do you charge extra for certain shapes? #NailShapes #NailTechLife #Poll', 'poll', 'nails', ARRAY['nail-shapes', 'nail-tech', 'poll'], ARRAY[], 67, 34, false, NOW() - INTERVAL '2 days'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'HELP! Lash retention issues 😭\n\nMy clients are losing 50% of their lashes within 2 weeks. I''m using quality adhesive, proper humidity (45-55%), good isolation... what am I missing?\n\nDetails:\n• 3 years experience\n• Classic and volume\n• Clients follow aftercare\n• Using speed-drying fans\n\nHas anyone dealt with this? Could it be seasonal? Product issue? I''m stumped! #LashProblems #LashRetention #HelpNeeded', 'question', 'lashes', ARRAY['lash-problems', 'retention-issues', 'help-needed'], ARRAY[], 43, 28, false, NOW() - INTERVAL '4 days'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'Makeup artists: How do you handle difficult clients? 🤔\n\nHad a bride last week who wanted to completely change her look 30 minutes before ceremony. She saw a Pinterest photo and insisted we recreate it exactly (it was a completely different skin tone and face shape).\n\nHow do you educate clients about realistic expectations while keeping them happy? Would love to hear your strategies! #MakeupArtist #ClientManagement #BridalStruggles', 'question', 'makeup', ARRAY['client-management', 'bridal-makeup', 'difficult-clients'], ARRAY[], 78, 51, false, NOW() - INTERVAL '6 days');

