INSERT INTO community_posts (id, user_id, content, post_type, category, tags, image_urls, likes_count, comments_count, is_featured, created_at) VALUES
(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'Asked @AI about fixing yellow-toned highlights and got this amazing advice! 🤯\n\n"Use a purple shampoo 2x/week, then tone with ash-based color 2 levels lighter than desired result. For stubborn brassiness, mix toner with 10vol developer and process for 15-20 minutes. Always deep condition after toning to prevent dryness."\n\nTried this on my client today and WOW! The yellow completely disappeared. AI is getting scary good at hair advice! 💜\n\n✨ Powered by EmviApp AI\n\n#AIBeautyTips #HairToning #YellowHair #AshBlonde', 'ai-tip', 'hair', ARRAY['ai-tips', 'hair-toning', 'purple-shampoo'], ARRAY[], 124, 47, false, NOW() - INTERVAL '1 day'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', '@AI just blew my mind with this nail art technique! Asked about creating perfect French tips and got this:\n\n"Use striping tape as a guide, apply base color first, remove tape while polish is slightly tacky for crisp lines. For curved French tips, use hole reinforcement stickers as templates. Clean up with small brush dipped in acetone."\n\nBeen doing nails for 5 years and never thought of the hole reinforcement trick! Tried it today - cleanest French tips ever! 🤍\n\n✨ Powered by EmviApp AI\n\n#AITips #FrenchNails #NailArt #NailHacks', 'ai-tip', 'nails', ARRAY['ai-tips', 'french-nails', 'nail-hacks'], ARRAY[], 89, 32, false, NOW() - INTERVAL '3 days'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'Hỏi @AI về cách làm môi không khô khi đeo khẩu trang, được response quá hay! 🎭\n\n"Sử dụng lip primer trước khi thoa son, chọn liquid lipstick dạng transfer-proof, dusting powder nhẹ qua giấy tissue, rồi spray setting spray từ xa 20cm. Kết thúc bằng lip balm ở góc môi để không bị khô."\n\nThử ngay hôm nay - đeo khẩu trang 8 tiếng mà son vẫn đẹp! AI hiểu beauty Việt Nam quá! 💋\n\n✨ Powered by EmviApp AI\n\n#AITips #KhauTrang #SonKhongTroi #VietnameseBeauty', 'ai-tip', 'makeup', ARRAY['ai-tips', 'mask-makeup', 'vietnamese-beauty'], ARRAY[], 156, 63, false, NOW() - INTERVAL '5 days'),

(gen_random_uuid(), '726a4454-a597-49f4-a4e6-42be6ba3da84', 'Mind = BLOWN! 🤯 Asked @AI about lash mapping for hooded eyes and got the most detailed response:\n\n"For hooded eyes, use shorter lengths (6-8mm) on inner corner, gradually increase to 10-12mm at center, then decrease slightly. Use C or CC curl to lift the hood. Focus volume in the center to create opening effect. Avoid dramatic lengths that will hit the hood when eyes are open."\n\nThis is exactly what I learned in my $500 advanced course! AI is becoming the ultimate beauty educator 🎓\n\n✨ Powered by EmviApp AI\n\n#AIEducation #LashMapping #HoodedEyes #LashTech', 'ai-tip', 'lashes', ARRAY['ai-education', 'lash-mapping', 'hooded-eyes'], ARRAY[], 198, 71, true, NOW() - INTERVAL '7 days');

